import axios from "axios";
import { useState } from "react";
import Tab from "react-bootstrap/Tab";
import Tabs from "react-bootstrap/Tabs";
const backend_host="localhost";
const backend_port=1212;
const Login = (props) => {
  const [authenticated, setAuthenticated] = useState(false);
  const [loginUsername, setLoginUsername] = useState("");
  const [signupUsername, setSignupUsername] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [signupPassword, setSignupPassword] = useState("");
  const [signupRepeatPassword, setSignupRepeatPassword] = useState("");
  const login_submit = () => {
  const cred_json={username:loginUsername,password:loginPassword};
    axios
      .post(`http://${backend_host}:${backend_port}/login`, cred_json, {
        headers: {
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*",
          Accept: "application/json",
        },
        withCredentials: false,
      })
      .then((result) => {
        if (result.data.uid != 0) {
          props.do_login(result.data.uid);
        }
      });
  };
  const signup_submit = () => {
    if (signupPassword===signupRepeatPassword) {
      const cred_json = { username: signupUsername, password: signupPassword };
      axios
        .post(`http://${backend_host}:${backend_port}/signup`, cred_json, {
          headers: {
            "Content-Type": "application/json",
            "Access-Control-Allow-Origin": "*",
            Accept: "application/json",
          },
          withCredentials: false,
        })
        .then((result) => {
          if (result.data.uid != 0) {
            props.do_login(result.data.uid);
          }
        });
    }

  };
  return (
    <div>
      <Tabs
        defaultActiveKey="login"
        id="uncontrolled-tab-example"
        className="mb-3"
      >
        <Tab eventKey="login" title="Login">
          <form>
            <div class="form-outline mb-4">
              <input
                type="text"
                onChange={(event) => setLoginUsername(event.target.value)}
                id="loginName"
                class="form-control"
              />
              <label class="form-label" for="loginName">
                Username
              </label>
            </div>

            <div class="form-outline mb-4">
              <input
                type="password"
                onChange={(event) => setLoginPassword(event.target.value)}
                id="loginPassword"
                class="form-control"
              />
              <label class="form-label" for="loginPassword">
                Password
              </label>
            </div>

            <div class="row mb-4">
              <div class="col-md-6 d-flex justify-content-center">
                <div class="form-check mb-3 mb-md-0">
                  <input
                    class="form-check-input"
                    type="checkbox"
                    value=""
                    id="loginCheck"
                    checked
                  />
                  <label class="form-check-label" for="loginCheck">
                    {" "}
                    Remember me{" "}
                  </label>
                </div>
              </div>

              <div class="col-md-6 d-flex justify-content-center">
                <a href="#!">Forgot password?</a>
              </div>
            </div>

            <button
              class="btn btn-primary btn-block mb-4"
              onClick={login_submit}
            >
              Sign in
            </button>

            <div class="text-center">
              <p>
                Not a member? <a href="#!">Register</a>
              </p>
            </div>
          </form>
        </Tab>
        <Tab eventKey="register" title="Register">
          <form>
            {/* <div class="form-outline mb-4">
              <input type="text" id="registerName" class="form-control" />
              <label class="form-label" for="registerName">
                Name
              </label>
            </div> */}

            <div class="form-outline mb-4">
              <input
                type="text"
                id="registerUsername"
                onChange={(event) => setSignupUsername(event.target.value)}
                class="form-control"
              />
              <label class="form-label" for="registerUsername">
                Username
              </label>
            </div>

            {/* <div class="form-outline mb-4">
              <input type="email" id="registerEmail" class="form-control" />
              <label class="form-label" for="registerEmail">
                Email
              </label>
            </div> */}

            <div class="form-outline mb-4">
              <input
                type="password"
                id="registerPassword"
                onChange={(event) => setSignupPassword(event.target.value)}
                class="form-control"
              />
              <label class="form-label" for="registerPassword">
                Password
              </label>
            </div>

            <div class="form-outline mb-4">
              <input
                type="password"
                onChange={(event) =>
                  setSignupRepeatPassword(event.target.value)
                }
                id="registerRepeatPassword"
                class="form-control"
              />
              <label class="form-label" for="registerRepeatPassword">
                Repeat password
              </label>
            </div>

            {/* <div class="form-check d-flex justify-content-center mb-4">
              <input
                class="form-check-input me-2"
                type="checkbox"
                value=""
                id="registerCheck"
                checked
                aria-describedby="registerCheckHelpText"
              />
              <label class="form-check-label" for="registerCheck">
                I have read and agree to the terms
              </label>
            </div> */}

            <button onClick={signup_submit} class="btn btn-primary btn-block mb-3">
              Sign Up
            </button>
          </form>
        </Tab>
      </Tabs>

    </div>
  );
};
export default Login;
